function Menu() {
  const[counter, setCounter] = useState(0)

  const increase = _ => setCounter(prev => prev++)

    return (
        <div className="card">
        <div className="card-body">
          <h5 className="card-title">Card title</h5>
          <p className="card-text">Counter {counter}</p>
          <a onClick={() => increase()} href="#" className="btn btn-primary">Go somewhere</a>
        </div>
        </div>
    )
  }

  export default Menu;
